declare module "love.audio" {
    /**
     * Units that represent time.
     *
     * | Constant | Description |
     * | :- | :- |
     * | `"seconds"` | Regular seconds. |
     * | `"samples"` | Audio samples. |
     *
     * @link [TimeUnit](https://love2d.org/wiki/TimeUnit)
     * @since 0.8.0
     */
    type TimeUnit = "seconds" | "samples";
}
